import axios from 'axios';
export default {
  name: 'MyComponent',
  data() {
    return {
      message: '',
      // 定义message变量，用于存储api返回的数据
      formData: {
        context: '',
        goal: '',
        style: '',
        tone: '',
        audience: '',
        output: ''
      }
    };
  },
  methods: {
    handleClick() {
      axios.get('http://localhost:8003/api/response/?format=json') //你的Django后端API的endpoint
      .then(response => {
        //在这里处理你的数据。response.data将包含你的数据。
        console.log(response.data);
        this.message = response.data.message;
      }).catch(error => {
        //在这里处理错误
        console.error(error);
      });
    },
    submitForm() {
      axios.post('http://localhost:8003/api/response/', this.formData).then(response => {
        // 处理你后端返回的response
        console.log(response);
        this.message = response.data.message;
      }).catch(err => {
        console.log(err);
      });
    }
  },
  mounted() {}
};
