// src/store/index.js
import { reactive } from 'vue';

// 创建一个响应式对象来存储登录状态
const state = reactive({
  isAuthenticated: false,
});

export default {
  state,
  login() {
    state.isAuthenticated = true;
  },
  logout() {
    state.isAuthenticated = false;
  },
};
