// store/index.js
import { defineStore } from 'pinia';

export const useAuthStore = definStore({
  id: 'auth',
  state: () => ({
    isAuthenticated: false,
  }),
  // 你可以添加 actions 和 mutations 等
   actions: {
     login() {
       this.isAuthenticated = true;
     },
     logout() {
       this.isAuthenticated = false;
     }
   }
});
