import "core-js/modules/es.array.push.js";
import { NButton, NInput, NForm, NFormItemRow } from 'naive-ui';
import { ref } from 'vue';
import { useStore } from 'vuex';
import { useRouter } from 'vue-router';
import axios from 'axios';

//通用变量

export default {
  __name: 'LoginForm',
  setup(__props, {
    expose: __expose
  }) {
    const authStore = useStore();
    const router = useRouter();

    //登录表单操作
    const loginUsername = ref('');
    const loginPassword = ref('');
    const keepLoggedInForDays = ref('7'); // 默认设置为7天，通过select控件让用户可选择

    const login = async () => {
      try {
        const response = await axios.post('/account/login/', {
          username: loginUsername.value,
          password: loginPassword.value,
          keep_logged_in_for_days: keepLoggedInForDays.value
        }, {
          withCredentials: true,
          headers: {
            //先注销'X-CSRFToken': csrfToken.value,
          }
        });
        const token = response.data.token.access;
        const user = response.data.username;
        const csrf_token = response.data.csrf_token;
        console.log('csrf_token:', csrf_token);
        localStorage.setItem('token', token);
        await authStore.commit('auth_success', {
          token,
          user
        });
        console.log("登录成功：", loginUsername.value);
        await router.push('/dashboard');
      } catch (err) {
        console.log("登录失败：", err);
      }
    };

    //暴露方法
    __expose({
      login
    });
    const __returned__ = {
      authStore,
      router,
      loginUsername,
      loginPassword,
      keepLoggedInForDays,
      login,
      get NButton() {
        return NButton;
      },
      get NInput() {
        return NInput;
      },
      get NForm() {
        return NForm;
      },
      get NFormItemRow() {
        return NFormItemRow;
      },
      ref,
      get useStore() {
        return useStore;
      },
      get useRouter() {
        return useRouter;
      },
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
