import "core-js/modules/es.array.push.js";
import { NFormItemRow, NForm, NButton, NInput } from 'naive-ui';
import { ref } from 'vue';
import { useRoute } from 'vue-router';
import axios from 'axios';

//注册表单操作

export default {
  __name: 'RegisterForm',
  setup(__props, {
    expose: __expose
  }) {
    const registerUsername = ref('');
    const registerPassword = ref('');
    //const registerRePassword=ref('');
    const invitationCode = ref('');
    const router = useRoute();
    const registeredNextPath = ref('');
    const register = async () => {
      try {
        const response = axios.post('/account/register/', {
          username: registerUsername,
          password: registerPassword,
          invitation_code: invitationCode
        }, {
          headers: {
            //'X-CSRFToken': csrfToken.value,
          }
        });
        console.log(response);
        console.log('注册成功！');
        await router.push(registeredNextPath.value);
      } catch (err) {
        console.log('注册失败:', err);
      }
    };

    //密码验证
    const formRef = ref(null);
    const rPasswordFormItemRef = ref(null);
    const modelRef = ref({
      age: null,
      password: null,
      reenteredPassword: null
    });
    const model = modelRef;
    const validatePasswordStartWith = (rule, value) => {
      return !!modelRef.value.password && modelRef.value.password.startsWith(value) && modelRef.value.password.length >= value.length;
    };
    const validatePasswordSame = (rule, value) => {
      return value === modelRef.value.password;
    };
    const rules = {
      password: [{
        required: true,
        message: "请输入密码"
      }],
      reenteredPassword: [{
        required: true,
        message: "请再次输入密码",
        trigger: ["input", "blur"]
      }, {
        validator: validatePasswordStartWith,
        message: "两次密码输入不一致",
        trigger: "input"
      }, {
        validator: validatePasswordSame,
        message: "两次密码输入不一致",
        trigger: ["blur", "password-input"]
      }]
    };
    const handlePasswordInput = () => {
      if (modelRef.value.reenteredPassword) {
        rPasswordFormItemRef.value?.validate({
          trigger: "password-input"
        });
      }
    };
    __expose({
      register
    });
    const __returned__ = {
      registerUsername,
      registerPassword,
      invitationCode,
      router,
      registeredNextPath,
      register,
      formRef,
      rPasswordFormItemRef,
      modelRef,
      model,
      validatePasswordStartWith,
      validatePasswordSame,
      rules,
      handlePasswordInput,
      get NFormItemRow() {
        return NFormItemRow;
      },
      get NForm() {
        return NForm;
      },
      get NButton() {
        return NButton;
      },
      get NInput() {
        return NInput;
      },
      ref,
      get useRoute() {
        return useRoute;
      },
      get axios() {
        return axios;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
